package com.wechat.jfinal.model;

import java.util.List;

import com.wechat.jfinal.model.base.BaseClassProductOrder;

/**
 * Generated by JFinal.
 */
@SuppressWarnings("serial")
public class ClassProductOrder extends BaseClassProductOrder<ClassProductOrder> {
	public static final ClassProductOrder dao = new ClassProductOrder().dao();

	public boolean hasBought(Integer student, Integer product) {

		boolean result = false;

		List<ClassProductOrder> classProductOrders = findByStudent(student);

		for (ClassProductOrder classProductOrder : classProductOrders) {
			if (product.equals(classProductOrder.getProductId())) {
				result = true;
			}
		}

		return result;

	}

	public List<ClassProductOrder> findByStudent(Integer student) {
		return find("select * from class_product_order where student_id = ?", student);
	}
}
